import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Home({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    // Fetch topics from the backend
    axios.get('http://localhost:5004/api/topics')
      .then(response => setTopics(response.data))
      .catch(error => console.error('Error fetching topics:', error));
  }, []);

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate('/login');
  };

  const handleTopicClick = (topicId) => {
    setSelectedTopic(topicId);
    // Fetch questions for the selected topic
    axios.get(`http://localhost:5004/api/questions/${topicId}`)
      .then(response => setQuestions(response.data))
      .catch(error => console.error('Error fetching questions:', error));
  };

  return (
    <div className="home-container">
      <nav className="navbar">
        <div className="navbar-title" onClick={() => navigate('/')}>App Title</div>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </nav>

      <div className="content">
        {/* Sidebar with Topics */}
        <div className="sidebar">
          <ul>
            {topics.map((topic) => (
              <li key={topic.id}>
                <button onClick={() => handleTopicClick(topic.id)}>
                  {topic.name}
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Main Content Area with Questions */}
        <div className="main-content">
          <h1>{selectedTopic ? `Questions for ${topics.find(t => t.id === selectedTopic)?.name}` : "Select a topic"}</h1>
          {questions.map((q) => (
            <div key={q.id} className="question-item">
              <h2>{q.question}</h2>
              <p>{q.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
