import ReactDOM from "react-dom/client";
import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Update from "./pages/Update";
import NoPage from "./pages/NoPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import './style.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState(""); // Store the logged-in user's name

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserName(""); // Clear the user's name on logout
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route index element={isLoggedIn ? <Home userName={userName} /> : <Login setIsLoggedIn={setIsLoggedIn} setUserName={setUserName} />} />
        <Route path="update" element={isLoggedIn ? <Update /> : <Login setIsLoggedIn={setIsLoggedIn} setUserName={setUserName} />} />
        <Route path="*" element={<NoPage />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} setUserName={setUserName} />} />
        <Route path="/register" element={<Register />} />
      </Routes>
    </BrowserRouter>
  );
}

// Render the app into the root element of your HTML
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
