import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login({ setIsLoggedIn, setUserName }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // State for error message
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Send a login request to the server
    const response = await fetch('http://localhost:5004/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    if (response.ok) {
      const data = await response.json();
      setIsLoggedIn(true);
      setUserName(data.username); // Set the username upon successful login
      navigate("/"); // Redirect to home page after successful login
    } else {
      const errorMessage = await response.text();
      setErrorMessage(errorMessage); // Set the error message instead of alert
      // Clear input fields on error
      setUsername('');
      setPassword('');
    }
  };

  const goToRegister = () => {
    navigate('/register'); // Navigate to the registration page
  };

  return (
    <div className="container login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button type="submit">Login</button>
      </form>

      {/* Display error message if it exists */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <button onClick={goToRegister} className="secondary">Register</button>
    </div>
  );
}

export default Login;
