import ReactDOM from "react-dom/client";
import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Update from "./pages/Update";
import NoPage from "./pages/NoPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import './style.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState(''); // Store username of logged-in user

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername(''); // Clear username on logout
  };

  return (
    <BrowserRouter>
      <Routes>
        {/* Home and Update routes are only accessible if logged in */}
        <Route 
          path="/" 
          element={isLoggedIn ? <Home username={username} handleLogout={handleLogout} /> : <Login setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />}
        />
        <Route 
          path="update" 
          element={isLoggedIn ? <Update /> : <Login setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />}
        />
        <Route path="*" element={<NoPage />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />} />
        <Route path="/register" element={<Register />} />
      </Routes>
    </BrowserRouter>
  );
}

// Render the app into the root element of your HTML
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
