import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [questions, setQuestions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch topics on component mount
    fetch('http://localhost:5004/api/topics')
      .then((response) => response.json())
      .then((data) => setTopics(data))
      .catch((error) => console.error('Error fetching topics:', error));
  }, []);

  const handleTopicClick = (topicId) => {
    setSelectedTopic(topicId);

    // Fetch questions for the selected topic
    fetch(`http://localhost:5004/api/questions/${topicId}`)
      .then((response) => response.json())
      .then((data) => setQuestions(data))
      .catch((error) => console.error('Error fetching questions:', error));
  };

  const handleLogout = () => {
    navigate('/login');
  };

  return (
    <div className="home-container">
      {/* Top Navbar with App Title on the Left and Logout Button on the Right */}
      <nav className="navbar">
        <div className="navbar-title">App Title</div>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </nav>

      <div className="content">
        {/* Left Sidebar with Tabs */}
        <div className="sidebar">
          <ul>
            {topics.map((topic) => (
              <li key={topic.id}>
                <button onClick={() => handleTopicClick(topic.id)}>{topic.name}</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Main Content Area - Display Questions */}
        <div className="main-content">
          {selectedTopic ? (
            <>
              <h2>Questions for Topic {selectedTopic}</h2>
              <ul>
                {questions.map((question) => (
                  <li key={question.id}>
                    <h3>{question.question}</h3>
                    <p>{question.answer}</p>
                  </li>
                ))}
              </ul>
            </>
          ) : (
            <p>Select a topic to see the questions.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
